import styled from "styled-components";

import Select from "components/Select";
import { saveDocumentInLocalStorage } from "utils/localStorage";

const INITIAL_SELECT_OPTIONS = [
  {
    name: "Diagram",
    value: "diagram",
  },
];

const Sidebar = ({
  elements,
  newNodeName,
  onInputChange,
  onSelectChange,
  selectedFlow,
}) => {
  const onDragStart = (event, nodeType) => {
    event.dataTransfer.setData("application/reactflow", nodeType);
    event.dataTransfer.effectAllowed = "move";
  };
  const handleSaveDocument = () => {
    saveDocumentInLocalStorage(elements);
  };

  return (
    <SideBarWrapper>
      <div className="flow-selector">
        <Select
          name="flow-selector"
          onChange={onSelectChange}
          options={INITIAL_SELECT_OPTIONS}
          placeholder="Select a saved flow"
          value={selectedFlow}
        />
      </div>
      <div className="nodes">
        <div className="description">
          You can drag these nodes to the workspace and connect them by clicking
          on the circular handles.
        </div>
        <div
          className="dndnode input"
          onDragStart={(event) => onDragStart(event, "input")}
          draggable
        >
          Input Node
        </div>
        <div
          className="dndnode"
          onDragStart={(event) => onDragStart(event, "default")}
          draggable
        >
          Default Node
        </div>
        <div
          className="dndnode output"
          onDragStart={(event) => onDragStart(event, "output")}
          draggable
        >
          Output Node
        </div>
      </div>
      <div className="actions">
        <div className="action save" onClick={handleSaveDocument}>
          Save
        </div>
        <div className="action clean">Clean workspace</div>
        <div className="action new">+ New</div>
        <div className="import-export-wrapper">
          <span className="action import">Import</span> /{" "}
          <span className="action export">Export</span> file
        </div>
      </div>
    </SideBarWrapper>
  );
};

export default Sidebar;

const SideBarWrapper = styled.div`
  box-shadow: 0 1px 30px 0 rgb(69 129 192 / 20%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  height: calc(100% - 60px);
  padding: 30px;
  width: 140px;

  .description {
    font-size: 12px;
    margin-bottom: 20px;
  }

  .actions {
    font-size: 14px;
    font-weight: bolder;

    .action {
      cursor: pointer;
      margin-bottom: 20px;
      white-space: nowrap;

      &:hover {
        color: #0041d0;
      }
    }
  }

  .dndnode {
    background: white;
    border-color: black;
    border-radius: 3px;
    border-style: solid;
    border-width: 1px;
    color: #222;
    cursor: grab;
    font-size: 12px;
    margin-bottom: 10px;
    padding: 10px;
    text-align: center;
    width: 150px;

    &:hover {
      box-shadow: 0 1px 4px 1px rgb(0 0 0 / 8%);
    }

    &.input {
      border-color: #0041d0;
    }

    &.output {
      border-color: #ff0072;
    }
  }
`;
