import styled from "styled-components";

import { saveDocumentInLocalStorage } from "utils/localStorage";

const Sidebar = ({ elements }) => {
  const onDragStart = (event, nodeType) => {
    event.dataTransfer.setData("application/reactflow", nodeType);
    event.dataTransfer.effectAllowed = "move";
  };

  const handleSaveDocument = () => {
    saveDocumentInLocalStorage(elements);
  };

  return (
    <SideBarWrapper>
      <div className="nodes">
        <div className="description">
          You can drag these nodes to the pane on the right.
        </div>
        <div
          className="dndnode input"
          onDragStart={(event) => onDragStart(event, "input")}
          draggable
        >
          Input Node
        </div>
        <div
          className="dndnode"
          onDragStart={(event) => onDragStart(event, "default")}
          draggable
        >
          Default Node
        </div>
        <div
          className="dndnode output"
          onDragStart={(event) => onDragStart(event, "output")}
          draggable
        >
          Output Node
        </div>
      </div>
      <div className="actions">
        <div className="action save" onClick={handleSaveDocument}>
          Save
        </div>
        <div className="action import">Import</div>
        <div className="action clean">Clean</div>
      </div>
    </SideBarWrapper>
  );
};

export default Sidebar;

const SideBarWrapper = styled.div`
  box-shadow: 0 1px 30px 0 rgb(69 129 192 / 20%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  height: calc(100% - 60px);
  padding: 30px;
  width: 140px;

  .description {
    font-size: 12px;
    margin-bottom: 20px;
  }

  .dndnode {
    border-color: black;
    border-radius: 3px;
    border-style: solid;
    border-width: 1px;
    color: #222;
    cursor: grab;
    font-size: 12px;
    margin-bottom: 20px;
    padding: 10px;
    text-align: center;
    width: 150px;

    &.input {
      border-color: #0041d0;
    }

    &.output {
      border-color: #ff0072;
    }
  }
`;
